mapper.select:
SELECT DP.*, T.NAME AS INSTRUCTOR_NAME FROM DAILY_PLAN DP LEFT JOIN TEACHER T ON (DP.TEACHER_ID = T.ID) WHERE SUBJECT LIKE CONCAT('%', #{subject}, '%')
AND DP.TEACHER_ID = (SELECT ID FROM TEACHER WHERE NAME = #{instructor})
AND ACTIVITY_DATE = #{date}

mapper.update:
UPDATE COUNSEL SET UPD_DATE = SYSDATE()
, CATEGORY = #{category}
, INSTRUCTOR_ID = #{instructorId}
, COUNSEL_DATE = #{counselDate}
, CHILD_NUM = #{childNum}
, COUNSEL_NOTE = #{counselNote}
WHERE NUM = #{num}

UPDATE HEALTH_CHECK SET DEL_YN = 'Y',
                        UPD_DATE = SYSDATE()
WHERE NUM = #{num}

UPDATE HEALTH_CHECK SET UPD_DATE = SYSDATE(),
                        HOSPITAL = #{hospital},
                        CHECK_DATE = #{checkDate},
                        CHECK_RESULT = #{checkResult}
WHERE NUM = #{num}

mapper.select:
SELECT AT.*, C.NAME AS CHILD_NAME
FROM ATTENDANCE AT
LEFT JOIN CHILD C ON (AT.CHILD_NUM = C.NUM and C.GRADUATED = 'N')
WHERE C.GRADE_NUM = #{grade}
  AND MONTH(ATT_DATE) = #{month}

SELECT COUNT(*)
FROM ATTENDANCE
WHERE CHILD_NUM = #{childNum}
  AND ATT_DATE = #{date}

mapper.update:
UPDATE ATTENDANCE SET STATUS = #{status},
                      NOTE = #{note}
WHERE CHILD_NUM = #{childNum}
  AND ATT_DATE = #{date}
