mapper.update:
UPDATE USER SET AUTH = #{auth},
                UPD_DATE = SYSDATE()
WHERE ID = #{id}

mapper.select:
SELECT *
FROM USER
WHERE ID = #{id}
  AND NAME = #{name}
  AND EMAIL = #{email}

